mapper.select:
SELECT a.id,a.receCode,a.isFrozen,a.aloneProportion, a.oneNumber,a.twoNumber,a.threeNumber,a.laterNumber, b.email,b.mobilePhone,b.country, kcl.levelName,kcl.gradation,kcl.rewardNum,kcl.pointAlgebra,ac.commendCode FROM app_commend_user AS a LEFT JOIN app_person_info AS b ON a.uid = b.customerId left join klg_customer_level kcl on a.uid = kcl.customerId left join app_customer ac on a.uid = ac.id
b.email like #{email,jdbcType=VARCHAR}
and b.mobilePhone like #{mobilePhone,jdbcType=VARCHAR}
and a.receCode like #{receCode,jdbcType=VARCHAR}
and ac.commendCode like #{commendCode,jdbcType=VARCHAR}

select id,email,mobilePhone,level,asset,recommended,allAsset,buyNumber,created from ( select ac.id,ap.email,ap.mobilePhone,al.gradeName level, IFNULL((ia.storageMoney+ia.coldMoney+ia.hotMoney),0.0000000000) asset, (select count(*) from app_commend_user au where ac.id=au.pid) recommended, (select IFNULL(sum(ia2.storageMoney+ia2.coldMoney+ia2.hotMoney),0.0000000000) from ico_account ia2 where ia2.customerId in (select uid from app_commend_user au2 where ac.id=au2.pid ) ) allAsset, (select IFNULL(sum(buyNumber),0.0000000000) buyNumber from ico_buy_order io where io.created BETWEEN date_sub(curdate(),interval 1 day) and date_sub(curdate(),interval 0 day) and io.customerId in (select uid from app_commend_user au2 where ac.id=au2.pid ) order BY io.customerId) buyNumber, (select MAX(created) created from ico_buy_order io where io.customerId in (select uid from app_commend_user au2 where ac.id=au2.pid ) order BY io.customerId) created from app_customer as ac LEFT JOIN ico_customer_level al on ac.id=al.customer_id LEFT JOIN app_person_info as ap on ac.id=ap.customerId LEFT JOIN ico_account as ia ON ac.id=ia.customerId
1 = 1
and ap.mobilePhone like #{mobilePhone,jdbcType=VARCHAR}
and al.gradeName = #{level,jdbcType=VARCHAR}
) t
1 = 1
and t.created >= #{startTime,jdbcType=DATE}
and t.created <= #{endTime,jdbcType=DATE}
ORDER BY t.created desc

SELECT ac.id,ap.email,ap.mobilePhone,al.gradeName level,ac.created, (ia.storageMoney+ia.coldMoney+ia.hotMoney) asset from (select au.uid id from app_commend_user au where au.pid= #{customerId,jdbcType=VARCHAR}) aus LEFT JOIN app_person_info ap ON aus.id=ap.customerId LEFT JOIN app_customer ac on aus.id=ac.id LEFT JOIN ico_account ia ON aus.id=ia.customerId LEFT JOIN ico_customer_level al on aus.id=al.customer_id
1 = 1
and ap.mobilePhone like #{mobilePhone,jdbcType=VARCHAR}
and ap.email like #{email,jdbcType=VARCHAR}
and al.gradeName = #{level,jdbcType=VARCHAR}
ORDER BY ac.created desc
