mapper.select:
select c.*, b.email, b.mobilePhone, b.surname, b.trueName, d.email as deliveryEmail, d.mobilePhone as deliveryMobilePhone from (SELECT a.custromerName, a.ordertNum, a.coinCode, a.feeamout, a.basemoney, a.rewardmoney, a.hierarchy, a.custromerId, a.transactionTime, a.deliveryId, a.userMoney, a.personType FROM app_commend_trade AS a
a.custromerId = #{cusomerId}
and a.deliveryId = #{deliveryId}
order by a.transactionTime desc limit #{start},#{end} ) as c LEFT JOIN app_person_info AS b ON c.custromerId = b.customerId left join app_person_info as d on c.deliveryId=d.customerId

SELECT count(*) FROM app_commend_trade AS a
a.custromerId = #{cusomerId}
and a.deliveryId = #{deliveryId}

SELECT b.customerId FROM app_person_info AS b
b.email = #{email,jdbcType=VARCHAR}
and b.mobilePhone = #{mobilePhone,jdbcType=VARCHAR}

select c.*, b.email, b.mobilePhone, b.surname, b.trueName, d.email as deliveryEmail, d.mobilePhone as deliveryMobilePhone from (SELECT a.custromerName, a.ordertNum, a.coinCode, a.feeamout, a.basemoney, a.rewardmoney, a.hierarchy, a.custromerId, a.transactionTime, a.deliveryId, a.userMoney, a.personType FROM app_commend_trade AS a
a.custromerId = #{cusomerId}
and a.deliveryId = #{deliveryId}
order by a.transactionTime desc ) as c LEFT JOIN app_person_info AS b ON c.custromerId = b.customerId left join app_person_info as d on c.deliveryId=d.customerId
1=1
and b.email like #{email}
and b.mobilePhone like #{mobilePhone}
and d.email like #{deliveryEmail}
and d.mobilePhone like #{deliveryMobilePhone}

select SUM(rewardmoney) as sumRewardmoney,coinCode,custromerId from app_commend_trade

	1=1

		 and created >= #{beginTime} 


		 and created < #{endTime} 


		and custromerId = #{custromerId}


		and coinCode = #{coinCode}

	and status=0

GROUP BY coinCode,custromerId;

