mapper.select:
        select e.entrustPrice,sum(e.surplusEntrustCount) as entrustCount,sum(e.surplusEntrustCount) as  surplusEntrustCount,sum(e.entrustSum-e.transactionSum) as entrustSum  from ex_entrust as e
        where

 e.coinCode=#{coinCode,jdbcType=VARCHAR}
	    and e.type = 1 and e.entrustWay =1 and e.status in (0,1)


	     group by e.entrustPrice  order by e.entrustPrice desc limit 0,#{count,jdbcType=INTEGER}

          select * from ex_entrust a


 a.coinCode=#{coinCode,jdbcType=VARCHAR}
		       and    a.type=1 and a.status<2
              and (a.entrustWay=2 or a.entrustWay=3 or(a.entrustWay=1 and entrustPrice<=#{lentrustPrice,jdbcType=VARCHAR} and entrustPrice>=#{gentrustPrice,jdbcType=DECIMAL}))


				and a.entrustNum in

		                  #{item}



				and a.entrustNum not in

		                  #{item}




             order by a.matchPriority desc,a.entrustPrice desc,a.entrustTime asc

          select * from ex_entrust a

                 a.type=2 and    a.status<2
                 a.coinCode=#{coinCode,jdbcType=VARCHAR}
                and (a.entrustWay=2 or a.entrustWay=3 or(a.entrustWay=1 and entrustPrice<=#{lentrustPrice,jdbcType=VARCHAR} and entrustPrice>=#{gentrustPrice,jdbcType=DECIMAL}))

	             and   a.customerType in

		                  #{item}


				and	a.entrustNum in

		                  #{item}



					and a.entrustNum not in

		                  #{item}



             order by a.matchPriority desc,a.entrustPrice asc,a.entrustTime asc

select min(a.entrustPrice) as minEntrustPrice,Max(a.entrustPrice)  as maxEntrustPrice ,sum(a.entrustCount) as entrustCountsum  from ex_entrust a


   a.coinCode=#{coinCode,jdbcType=VARCHAR}
      and a.type=#{type} and a.status<2

     and   a.customerType in

              #{item}

SELECT #{startPrice} as startPrice, #{minPrice} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE coinCode=#{coinCode,jdbcType=VARCHAR} and entrustPrice >= endPrice AND entrustPrice <= startPrice and type = 2 and entrustWay =1 and status in (0,1) and customerType in
#{item}
) AS surplusEntrustCount union all select #{startPrice}+1*#{depthjg} as startPrice, #{startPrice} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE coinCode=#{coinCode,jdbcType=VARCHAR} and entrustPrice > endPrice AND entrustPrice <= startPrice and type = 2 and entrustWay =1 and status in (0,1) and customerType in
#{item}
) AS surplusEntrustCount union all select #{startPrice}+2*#{depthjg} as startPrice, #{startPrice}+1*#{depthjg} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE coinCode=#{coinCode,jdbcType=VARCHAR} and entrustPrice > endPrice AND entrustPrice <= startPrice and type = 2 and entrustWay =1 and status in (0,1) and customerType in
#{item}
) AS surplusEntrustCount
union all select #{startPrice}+3*#{depthjg} as startPrice, #{startPrice}+2*#{depthjg} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE coinCode=#{coinCode,jdbcType=VARCHAR} and entrustPrice > endPrice AND entrustPrice <= startPrice and type = 2 and entrustWay =1 and status in (0,1) ) AS surplusEntrustCount union all select #{startPrice}+4*#{depthjg} as startPrice, #{startPrice}+3*#{depthjg} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE coinCode=#{coinCode,jdbcType=VARCHAR} and entrustPrice > endPrice AND entrustPrice <= startPrice and type = 2 and entrustWay =1 and status in (0,1) ) AS surplusEntrustCount

SELECT #{startPrice} as startPrice, #{maxPrice} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE coinCode=#{coinCode,jdbcType=VARCHAR}
and entrustPrice >= startPrice AND entrustPrice <= endPrice
and type = 1 and entrustWay =1 and status in (0,1) and customerType in
#{item}
) AS surplusEntrustCount union all select #{startPrice}-1*#{depthjg} as startPrice, #{startPrice} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE coinCode=#{coinCode,jdbcType=VARCHAR} and entrustPrice >= startPrice AND entrustPrice < endPrice and type = 1 and entrustWay =1 and status in (0,1) and customerType in
#{item}
) AS surplusEntrustCount union all select #{startPrice}-2*#{depthjg} as startPrice, #{startPrice}-1*#{depthjg} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE coinCode=#{coinCode,jdbcType=VARCHAR} and entrustPrice >= startPrice AND entrustPrice < endPrice and type = 1 and entrustWay =1 and status in (0,1) and customerType in
#{item}
) AS surplusEntrustCount
union all select #{startPrice}-3*#{depthjg} as startPrice, #{startPrice}-2*#{depthjg} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE coinCode=#{coinCode,jdbcType=VARCHAR} and entrustPrice >= startPrice AND entrustPrice < endPrice and type = 1 and entrustWay =1 and status in (0,1) and customerType in
#{item}
) AS surplusEntrustCount union all select #{startPrice}-4*#{depthjg} as startPrice, #{startPrice}-3*#{depthjg} as endPrice, ( SELECT sum(surplusEntrustCount) FROM ex_entrust WHERE
coinCode=#{coinCode,jdbcType=VARCHAR}
and entrustPrice >= startPrice AND entrustPrice < endPrice
and type = 1 and entrustWay =1 and status in (0,1) and customerType in
#{item}
) AS surplusEntrustCount

SELECT entrustNum, entrustTime, source, entrustPrice, entrustCount, type, surplusEntrustCount, status, transactionSum, processedPrice, entrustSum, fixPriceCoinCode, coinCode FROM ex_entrust where 1=1
and customerId = #{customerId}
and coinCode = #{coinCode}
and fixPriceCoinCode = #{fixPriceCoinCode}
and status in (0,1)
and status in (2,3,4)
and type =#{typeone}
and entrustTime >= #{startTime}
and entrustTime <= #{endTime}
order by entrustTime desc

select e.id,e.entrustPrice,e.entrustCount,e.status,e.type,e.entrustWay,entrustSum,
e.surplusEntrustCount,e.floatUpPrice,floatDownPrice,entrustNum,website,currencyType,
transactionFeeRate,transactionFee,transactionSum,fixPriceType,customerId,coinCode,fixPriceCoinCode,userName from ex_entrust as e where e.id =#{id}


  	 select * from ex_entrust  where
	 customerId=#{customerId,jdbcType=BIGINT}
	 and coinCode  = #{coinCode}
	 and fixPriceCoinCode  = #{fixPriceCoinCode}
	 and status between 2 and 4
	 and isType=#{isType}
	 order by created desc
     limit 0,#{counted}


	 select * from ex_entrust
	 where
	 customerId=#{customerId,jdbcType=BIGINT}
	 and coinCode  = #{coinCode}
	 and fixPriceCoinCode  = #{fixPriceCoinCode}
	 and status between 0 and 1
	 and isType=#{isType}
	 order by created desc
     limit 0,#{counting}

SELECT a.id, a.entrustTime, a.coinCode, a.fixPriceCoinCode, a.type, a.entrustWay, a.entrustPrice, a.entrustCount, a.entrustSum, a.surplusEntrustCount, a.processedPrice, a.entrustNum, a.status, a.source, a.customerIp, a.transactionFee, a.transactionFeeRate, a.transactionSum, b.email, b.mobilePhone, b.country, b.trueName, b.surname
FROM ex_entrust AS a LEFT JOIN app_person_info AS b ON a.customerId = b.customerId
1=1
and a.status in (0,1)
and a.status in (2,3,4)
and a.status = 0
and a.status = 1
and a.status = 2
and a.status = 3
and a.status = 4
and a.entrusStatus = #{entrusStatus,jdbcType=VARCHAR}
and a.entrustWay = #{entrustWay,jdbcType=VARCHAR}
and a.type = #{entrustType,jdbcType=VARCHAR}
and a.source = #{source,jdbcType=VARCHAR}
and a.coinCode = #{coinCode,jdbcType=VARCHAR}
and a.fixPriceCoinCode = #{fixPriceCoinCode,jdbcType=VARCHAR}
and b.email like #{email,jdbcType=VARCHAR}
and b.mobilePhone like #{mobilePhone,jdbcType=VARCHAR}
and b.trueName like #{trueName,jdbcType=VARCHAR}
and b.surname like #{surname,jdbcType=VARCHAR}
order by a.entrustTime desc

SELECT entrust.*, b.email, b.mobilePhone, b.country, b.trueName, b.surname FROM (SELECT a.id, a.customerId, a.entrustTime, a.coinCode, a.fixPriceCoinCode, a.type, a.entrustWay, a.entrustPrice, a.entrustCount, a.entrustSum, a.surplusEntrustCount, a.processedPrice, a.entrustNum, a.status, a.source, a.customerIp, a.transactionFee, a.transactionFeeRate, a.transactionSum FROM ex_entrust AS a
1=1
and a.status in (0,1)
and a.status in (2,3,4)
and a.status = 0
and a.status = 1
and a.status = 2
and a.status = 3
and a.status = 4
and a.entrusStatus = #{entrusStatus,jdbcType=VARCHAR}
and a.entrustWay = #{entrustWay,jdbcType=VARCHAR}
and a.type = #{entrustType,jdbcType=VARCHAR}
and a.source = #{source,jdbcType=VARCHAR}
and a.coinCode = #{coinCode,jdbcType=VARCHAR}
and a.fixPriceCoinCode = #{fixPriceCoinCode,jdbcType=VARCHAR}
and a.entrustNum = #{entrustNum}
and a.customerId in
#{item}
and a.entrustTime >= #{entrustTimeEgt}
and a.entrustTime <= #{entrustTimeElt}
order by a.entrustTime desc limit #{start},#{end} ) AS entrust LEFT JOIN app_person_info AS b ON entrust.customerId = b.customerId

